catalog_ui_policy.script_false:
function onCondition() {

	g_form.setDisplay('variables.validity_in_years', false);
	g_form.setDisplay('variables.cost_center', false);
	g_form.setDisplay('variables.subject_alternative_names', true);
	g_form.setDisplay('variables.certificate_type_external', false);
	g_form.setDisplay('variables.certificate_type_internal', true);

}

catalog_ui_policy.script_true:
function onCondition() {
	g_form.setDisplay('variables.validity_in_years', true);
	g_form.setDisplay('variables.cost_center', true);
	g_form.setDisplay('variables.subject_alternative_names', false);
	g_form.setDisplay('variables.certificate_type_external', true);
	g_form.setDisplay('variables.certificate_type_internal', false);
}
