sys_script.script:
(function executeRule(current,previous/*null when async*/ ) {
	var dev_name =  current.name;
	var is_add = "" ;
	var grinv = new GlideRecord('x_avx_appviewx_aut_application_delivery_controllers');
		grinv.initialize();
		grinv.addQuery('name',dev_name);
		grinv.setLimit(1);
		grinv.query();
		while(grinv.next()){
			if(grinv.name == dev_name){
				gs.addErrorMessage("Asset name already exists. Try adding with different name.");
				current.setAbortAction(true);
				is_add = "No";
			}
		}
	var grinv2 = new GlideRecord('x_avx_appviewx_aut_other_device');
		grinv2.initialize();
		grinv2.addQuery('name',dev_name);
		grinv2.setLimit(1);
		grinv2.query();
		while(grinv2.next()){
			if(grinv2.name == dev_name){
				gs.addErrorMessage("Asset name already exists. Try adding with different name.");
				current.setAbortAction(true);
				is_add = "No";
			}
		}
	if(is_add == "" && current.device_in_appviewx == 'Add'){
		var r = new sn_ws.RESTMessageV2();
		var url = gs.getProperty("x_avx_appviewx_aut.AppViewX URL");
		var username = gs.getProperty("x_avx_appviewx_aut.Username");
		var password = gs.getProperty("x_avx_appviewx_aut.Password");
		r.setEndpoint(url+"/avxapi/adc-add-device?gwkey=f000ca01&gwsource=web");
		r.setHttpMethod("post");
		r.setRequestHeader("username", username);
		r.setRequestHeader("password", password);
		r.setRequestHeader("Content-Type", "application/json");
		var decrypted = current.adc_password.getDecryptedValue();
		var modules = current.modules.split(",");
		var payload = {"payload":{"primary":[{"vendor":"","access":[{"userName":" ","password":"","accessType":""}],"module":["LTM"],"deviceType":[],"deviceCommunicationPort":{"sshPort":"22"},"isHostedOnVcmp":false,"isHostedOnHypervisor":false,"autoSyncEnabled":true,"name":"","dataCenter":"","communicationAddressType":"ip","ip":"","manageCertificate":"Managed","credentialType":"Manual Entry","tokenBasedAuthentication":false,"_id":"","category":"ADC","subsystem":"LoadBalancer","status":"Inprogress","syncFailoverGroupName":"","isAutoDetect":true,"noDetect":false}],"secondary":[]}};
		payload.payload.primary[0].access[0].userName = current.username.toString();
		payload.payload.primary[0].access[0].password = gs.base64Encode(decrypted.toString());
		payload.payload.primary[0].module = modules;
		payload.payload.primary[0].ip = current.ip_address.toString();
		payload.payload.primary[0].vendor = current.adc_vendor.toString();
		payload.payload.primary[0].name = current.name.toString();
		var pay = JSON.stringify(payload);
		r.setRequestBody(pay);
		r.setHttpTimeout(5000);
		var response = r.executeAsync();
		var responseBody,responseMessage;
		if (response.haveError())
		{
			responseMessage = response.getErrorMessage();
			responseBody = response.getBody();
			current.device_in_appviewx = responseMessage;
			gs.addErrorMessage("Failed to add device - "+ responseMessage);
		}
		else 
		{
			current.device_in_appviewx = "Added into AppViewX";
			gs.addInfoMessage("Device added succesfully in AppViewX");		
		}

	}
	else if(is_add == "" &&  current.device_in_appviewx == 'No'){
		current.device_in_appviewx = "Not Added";

	}

	else{
		//
	}
})(current,previous);
