sys_script.script:
(function executeRule(current,previous/*null when async*/ ) {
			var r = new sn_ws.RESTMessageV2();
			var commonName = current.hierarchical_variables.common_name;
			var device_type = current.hierarchical_variables.device_type;
			var device_name = current.hierarchical_variables.device;
			var ssl_profile_dr = current.hierarchical_variables.ssl_profile_dr;
			var obj_bind = [{"device_name":device_name,"ssl_profile_dr":ssl_profile_dr}];
			var url = gs.getProperty("x_avx_appviewx_aut.AppViewX URL");
			var username = gs.getProperty("x_avx_appviewx_aut.Username");
			var password = gs.getProperty("x_avx_appviewx_aut.Password");
			r.setEndpoint(url+"/avxapi/visualworkflow-submit-request?gwkey=f000ca01&gwsource=web");
			r.setHttpMethod("post");
			r.setRequestHeader("username", username);
			r.setRequestHeader("password", password);
			r.setRequestHeader("Content-Type", "application/json");
			var payload;
			payload = "{\"payload\":{\"data\":{\"input\":{\"requestData\":[{\"sequenceNo\":1,\"scenario\":\"scenario\",\"fieldInfo\":{\"ritm\":\""+current.number+"\",\"san\":\""+commonName+"\",\"commonName\":\""+commonName+"\",\"device_name\":\""+device_name+"\",\"ssl_profile_dr\":\""+ssl_profile_dr+"\",\"device_type\":\""+device_type+"\",\"obj_bind\":[{\"device_name\":\""+device_name+"\",\"ssl_profile_dr\":\""+ssl_profile_dr+"\"}]}}]},\"task_action\":1},\"header\":{\"workflowName\":\"Easy Certificate Provisioning\"}}}";
			r.setRequestBody(payload);
			r.setHttpTimeout(10000);
			var response = r.executeAsync();
			var responseBody,responseMessage;
			var gr = new GlideRecord('x_avx_appviewx_aut_logs');
				gr.initialize();
				gr.level = 'Info';
				gr.script = 'appviewx_sc_easy_cert_provisioning';
				gr.application = 'AppViewX';
				gr.message = 'Triggerring request API for catalog request with number ' + current.number;
				gr.insert();
			if (response.haveError())
				{
						responseMessage = response.getErrorMessage();
						responseBody = response.getBody();
						var res = JSON.stringify(responseBody);
						gr.level = 'Error';
						gr.script = 'appviewx_sc_infoblox_record_creation';
						gr.application = 'AppViewX';
						gr.message = 'Reponse for the request with number ' + current.number + ' ' + responseBody + ' Message: ' + responseMessage ;
						gr.insert();
						current.setValue('request_response',res);
						current.hierarchical_variables.request_response = res;
						current.update();

				}
			else 
				{
					responseBody = response.getBody();
						gr.level = 'Info';
						gr.script = 'appviewx_sc_easy_cert_provisioning';
						gr.application = 'AppViewX';
						gr.message = 'Reponse for the request with number ' + current.number + responseBody ;
						gr.insert();
					current.setValue('request_response',res);
					current.hierarchical_variables.request_response = res;
					current.update();



				}

			}		
)(current,previous);
