sys_script_client.script:
function onSubmit() {
	if(g_form.isNewRecord()){
       var ans = confirm("Do you want to add device into AppViewX?");
		if (ans){
			var ga = new GlideAjax('create_adc_in_appviewx');
			ga.addParam('sysparm_name', 'create_adc');
			ga.addParam('sysparm_device_name', g_form.getValue('name'));
			ga.addParam('sysparm_username', g_form.getValue('username'));
			ga.addParam('sysparm_password', g_form.getValue('password'));
			ga.addParam('sysparm_ip', g_form.getValue('ip'));
			ga.addParam('sysparm_modules', g_form.getValue('modules'));
			ga.addParam('sysparm_vendor', g_form.getValue('vendor'));
			ga.getXML(Parse);
		}   
	}
function Parse(response) {
	var answer = response.responseXML.documentElement.getAttribute("answer");

}
}
