sys_script_client.script:
function onSubmit() {
	if(g_form.isNewRecord()){
       var ans = confirm("Do you want to add device into AppViewX?");
		if (ans){
			var ga = new GlideAjax('create_adc_in_appviewx');
			ga.addParam('sysparm_name','addAdcDevice');
			ga.addParam('sysparm_device_name', g_form.getValue('name'));
			ga.addParam('sysparm_username', g_form.getValue('username'));
			ga.addParam('sysparm_password', g_form.getValue('adc_password'));
			ga.addParam('sysparm_ip', g_form.getValue('ip_address'));
			ga.addParam('sysparm_modules', g_form.getValue('modules'));
			ga.addParam('sysparm_vendor', g_form.getValue('adc_vendor'));
			ga.addParam('sysparm_number',g_form.getValue('number'));
			ga.getXML(callBackFunction);
			g_form.setValue('device_in_appviewx','Yes');

		}   
	}
	function callBackFunction(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		if(answer == "Done"){
				g_form.setValue('device_in_appviewx','Yes');
			}
	}
}
