sys_script_include.script:
var delete_device = Class.create();
delete_device.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	initialize: function() {},
	adc: function(device,sys_id) {
		try {
			gs.info("Deleting Device from AppViewX");
			var r = new sn_ws.RESTMessageV2();
			var url = gs.getProperty("x_avx_appviewx_aut.AppViewX URL");
			var username = gs.getProperty("x_avx_appviewx_aut.Username");
			var password = gs.getProperty("x_avx_appviewx_aut.Password");
			var dev = this.getParameter('sysparam_device');
			var payload = {"payload":{"name":[]}};
			payload.payload.name = device.split(",");
			r.setEndpoint(url+"/avxapi/adc-delete-device?gwkey=f000ca01&gwsource=web");
			r.setHttpMethod("post");
			r.setRequestHeader("username", username);
			r.setRequestHeader("password", password);
			r.setRequestHeader("Content-Type", "application/json");
			var pay = JSON.stringify(payload);
			r.setRequestBody(pay);
			r.setHttpTimeout(10000);
			var gr = new GlideRecord('x_avx_appviewx_aut_logs');
				gr.initialize();
				gr.level = 'Info';
				gr.script = 'x_avx_appviewx_aut.delete_device';
				gr.application = 'AppViewX';
				gr.message = 'Deleting device ' + device + ' from AppViewX';
				gr.insert();
			var response = r.executeAsync();
			var status  = response.getStatusCode();
			var responseBody;
			if (status == 200) {
				responseBody = response.getBody();
					gr.message = "Device " + device + " deleted successfully";
					gr.insert();
				}
			else {
					gr.message = 'Error while deleting the device -  ' + data[response];
					gr.insert();
				}
			}
		catch (exe) {
				gr.message = 'Error while deleting the device. Response - ' + exe;
				gr.insert();
		}
	},
    type: 'delete_device'
});
