sys_ui_policy.script_false:
function onCondition() {
	g_form.clearOptions('certificate_type');
	g_form.addOption('certificate_type','SSL Plus','SSL Plus');
	g_form.addOption('certificate_type','SSL Wildcart','SSL Wildcart');
	g_form.addOption('certificate_type','SSL EV Plus','SSL EV Plus');
	g_form.addOption('certificate_type','SSL Multi Domain','SSL Multi Domain');
}

sys_ui_policy.script_true:
function onCondition() {
	var cert = g_form.getValue('certificate_authority');
	g_form.clearOptions('certificate_type');
	if(cert == "Sectigo"){
// 		g_form.clearOptions('certificate_type');
		g_form.addOption('certificate_type','Sectigo Multi-Domain SSL','Sectigo Multi-Domain SSL');
	}
	else if(cert == "GoDaddy"){
// 		g_form.clearOptions('certificate_type');
		g_form.addOption('certificate_type','Standard SSL (DV_SSL)','Standard SSL (DV_SSL)');
		g_form.addOption('certificate_type','Deluxe SSL (OV_SSL)','Deluxe SSL (OV_SSL)');
		g_form.addOption('certificate_type','Premium SSL (EV_SSL)','Premium SSL (EV_SSL)');
	}
	else if(cert == "DigiCert"){
// 		g_form.clearOptions('certificate_type');
		g_form.addOption('certificate_type','SSL Plus','SSL Plus');
		g_form.addOption('certificate_type','SSL Wildcart','SSL Wildcart');
		g_form.addOption('certificate_type','SSL EV Plus','SSL EV Plus');
		g_form.addOption('certificate_type','SSL Multi Domain','SSL Multi Domain');
	}

}
